using NUnit.Framework;

namespace _468.ValidateIPAddress.Tests;

public class Tests
{
    private Solution _sut;

    [SetUp]
    public void Setup()
    {
        _sut = new Solution();
    }

    [TestCase("...", "Neither")]
    [TestCase("", "Neither")]
    [TestCase(":::::::", "Neither")]
    [TestCase("172.16.254.1", "IPv4")]
    [TestCase("192.168.1.0", "IPv4")]
    [TestCase("192.168.1.1", "IPv4")]
    [TestCase("256.256.256.256", "Neither")]
    [TestCase("1111.255.255.255", "Neither")]
    [TestCase("172.16.254.01", "Neither")]
    [TestCase("172.16.254.00", "Neither")]
    [TestCase("2001:0db8:85a3:0:0:8A2E:0370:733", "IPv6")]
    [TestCase("2001:0db8:85a3:0000:0000:8a2e:0370:7334", "IPv6")]
    [TestCase("2001:db8:85a3:0:0:8A2E:0370:7334", "IPv6")]
    [TestCase("2001:0db8:85a3::8A2E:037j:7334", "Neither")]
    [TestCase("02001:0db8:85a3:0000:0000:8a2e:0370:7334", "Neither")]
    [TestCase("...", "Neither")]
    public void ShouldReturnValidatedIpAddress(string queryIP, string expected)
    {
        var result = _sut.ValidIPAddress(queryIP);

        Assert.AreEqual(expected, result);
    }
}